import React from "react";

import styles from "./MealsItem.module.css";

const MealsItem = (props) => {
  const price = `$${props.price.toFixed(2)}`;
  return (
    <li>
      <div>
        <h3>{props.name}</h3>
      </div>
      <div>{props.description}</div>
      <div>{price}</div>
    </li>
  );
};

export default MealsItem;
